{
  "steps": [
    {
      "agent": "SocialMediaAnalyst",
      "goal": "Search for SNS posts (X/Twitter, LinkedIn, etc.) regarding OpenCLO/OpenClaw security vulnerabilities (CVE-2026-25253) and summarize the public reaction, community concerns, and shared insights.",
      "deliverable": "A structured summary of SNS discussions including common sentiments and key points mentioned by users.",
      "tool_hints": [
        "scrape_sns_with_mcp"
      ]
    },
    {
      "agent": "MainAgent",
      "goal": "Post the SNS summary provided by the SocialMediaAnalyst to the 'agentic' Slack channel.",
      "deliverable": "Confirmation of the Slack message delivery with a timestamp or success message.",
      "tool_hints": [
        "slack_post_message"
      ]
    }
  ],
  "notes": "SocialMediaAnalyst will identify the most relevant social signals, and MainAgent will handle the final delivery to the specified Slack channel."
}
